    void createAudioRecorder() {
        int sampleRate = 11025;
        int chanellConfig = AudioFormat.CHANNEL_IN_MONO;
        int audioFormat = AudioFormat.ENCODING_PCM_16BIT;
        int minInternalBuffSize = AudioRecord.getMinBufferSize(sampleRate, chanellConfig, audioFormat);
        int internalBuffSize = minInternalBuffSize * 4;
        Log.d(TAG, "minInternalBuffSize " + minInternalBuffSize + " internalBuffSize " + internalBuffSize);
        audioRecord = new AudioRecord(MediaRecorder.AudioSource.MIC, sampleRate, chanellConfig, audioFormat, internalBuffSize);
    }

